				}
			}

			if ($this->aNightAudit !== null) {
				if (!$this->aNightAudit->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aNightAudit->getValidationFailures());
				}
			}


			if (($retval = CashBoxPeer::doValidate($this, $columns)) !== true) {
				$failureMap = array_merge($failureMap, $retval);
			}


				if ($this->collMovementCashboxs !== null) {
