		if ($this->isColumnModified(CashBoxPeer::CREATED_AT)) $criteria->add(CashBoxPeer::CREATED_AT, $this->created_at);
		if ($this->isColumnModified(CashBoxPeer::UPDATED_AT)) $criteria->add(CashBoxPeer::UPDATED_AT, $this->updated_at);
