	 * @param      PropelPDO $con the connection to use
	 * @return     GradeSubjectPeriod
	 */
	public static function retrieveByPK($pk, PropelPDO $con = null)
	{

		if (null !== ($obj = GradeSubjectPeriodPeer::getInstanceFromPool((string) $pk))) {
			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(GradeSubjectPeriodPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$criteria = new Criteria(GradeSubjectPeriodPeer::DATABASE_NAME);
		$criteria->add(GradeSubjectPeriodPeer::ID, $pk);

		$v = GradeSubjectPeriodPeer::doSelect($criteria, $con);

		return !empty($v) > 0 ? $v[0] : null;
	}

	/**
